"use strict";

var verifyEmailHTML = function verifyEmailHTML(name, verificationLink, verificationCode) {
  return "\n<div style=\"max-width: 600px; margin: auto; background-color: #f4f4f4; padding: 20px; border-radius: 10px; box-shadow: 0 2px 4px rgb(104, 182, 255);\">\n  <div style=\"background-color: #ffffff; padding: 20px; border-radius: 10px;\">\n    <img src=\"https://raw.githubusercontent.com/nz-m/SocialEcho/main/client/src/assets/SocialEcho.png\" alt=\"SocialEcho Logo\" style=\"display: block; margin: auto auto 20px;max-width: 50%;\">\n    <p style=\"font-size: 18px; margin-bottom: 20px; text-align: center; color: #4b5563; font-weight: bold;\">Welcome to SocialEcho, ".concat(name, "!</p>\n    <p style=\"font-size: 16px; margin-bottom: 20px; text-align: center; color: #4b5563;\">Please click the button below to verify your email address and activate your account:</p>\n    <div style=\"text-align: center; margin-bottom: 20px;\">\n      <a href=\"").concat(verificationLink, "\" style=\"background-color: #3b82f6; color: #ffffff; padding: 12px 25px; border-radius: 5px; text-decoration: none; display: inline-block; font-size: 16px; font-weight: bold;\">Verify Email Address</a>\n   </div>\n    <p style=\"font-size: 14px; margin-bottom: 20px; text-align: center; color: #4b5563;\">Please note that the device you are using for this verification process will be set as your primary device.</p>\n    <p style=\"font-size: 14px; margin-bottom: 20px; text-align: center; color: #6b7280;\">The link will expire in 30 minutes.</p>\n    <p style=\"font-size: 16px; margin-bottom: 15px; text-align: center; color: #3b82f6; font-weight: bold;\">Your verification code is: <span style=\"color: #000000;\">").concat(verificationCode, "</span></p>\n    <p style=\"font-size: 14px; margin-bottom: 20px; text-align: center; color: #4b5563;\">If you did not create an account, please ignore this email.</p>\n   </div>\n</div>");
};
var verifyLoginHTML = function verifyLoginHTML(name, verificationLink, blockLink, currentContextData) {
  return "\n  <div style=\"background-color: #F4F4F4; padding: 20px;\">\n    <div style=\"background-color: #fff; padding: 20px; border-radius: 10px;\">\n      <h1 style=\"color: black; font-size: 24px; margin-bottom: 20px;\">New login attempt detected</h1>\n      <p>Dear ".concat(name, ",</p>\n      <p>Our system has detected that a new login was attempted from the following device and location at ").concat(currentContextData.time, ":</p>\n      <ul style=\"list-style: none; padding-left: 0;\">\n        <li><strong>IP Address:</strong> ").concat(currentContextData.ip, "</li>\n        <li><strong>Location:</strong> ").concat(currentContextData.city, ", ").concat(currentContextData.country, "</li>\n        <li><strong>Device:</strong> ").concat(currentContextData.device, " ").concat(currentContextData.deviceType, "</li>\n        <li><strong>Browser:</strong> ").concat(currentContextData.browser, "</li>\n        <li><strong>Operating System:</strong> ").concat(currentContextData.os, "</li>\n        <li><strong>Platform:</strong> ").concat(currentContextData.platform, "</li>\n      </ul>\n      <p>If this was you, please click the button below to verify your login:</p>\n      <div style=\"text-align: center;\">\n        <a href=\"").concat(verificationLink, "\" style=\"display: inline-block; padding: 10px 20px; background-color: #1da1f2; color: #fff; text-decoration: none; border-radius: 5px; margin-bottom: 20px;\">Verify Login</a>\n      </div>\n      <p>If you believe this was an unauthorized attempt, please click the button below to block this login:</p>\n      <div style=\"text-align: center;\">\n        <a href=\"").concat(blockLink, "\" style=\"display: inline-block; padding: 10px 20px; background-color: #E0245E; color: #fff; text-decoration: none; border-radius: 5px; margin-bottom: 20px;\">Block Login</a>\n      </div>\n      <p>Please verify that this login was authorized. If you have any questions or concerns, please contact our customer support team.</p>\n    </div>\n  </div>\n");
};
module.exports = {
  verifyEmailHTML: verifyEmailHTML,
  verifyLoginHTML: verifyLoginHTML
};